import React, { Component } from 'react';
import './App.css';


class App extends Component {

  constructor(props){
    super(props);
    this.state={
      title: 'React Application',
      act: 0,
      index: '',
      datas: []
    }
  } 



  fSubmit = (e) =>{
    e.preventDefault();
    console.log('try');

    let datas = this.state.datas;
    let appkey = this.refs.appkey.value;

    if(this.state.act === 0){   //new
      let data = {
        appkey
      }
      datas.push(data);
    }else{                      //update
      let index = this.state.index;
      datas[index].appkey = appkey;

    }    

    this.setState({
      datas: datas,
      act: 0
    });

    this.refs.myForm.reset();
    this.refs.appkey.focus();
  }


  render() {
    let datas = this.state.datas;
    return (
      <div className="App">
        <h2>{this.state.title}</h2>
        <form ref="myForm" className="myForm">
          <input type="text" ref="enter appkey" placeholder="enter appkey" className="formField" />

          <button onClick={(e)=>this.fSubmit(e)} className="myButton">submit </button>
        </form>
      </div>
    );
  }
}



export default App;
