import React, { Component } from "react";
import "./App.css";
import "./server.js"



const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      appkey: null,

      formErrors: {
        appkey: ""
      }
    };
  }

  handleSubmit = e => {
    e.preventDefault();

    if (formValid(this.state)) {
      console.log(`
        --SUBMITTING--
        First Name: ${this.state.appkey}

      `);
    } else {
      console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
    }
  };


  render() {
    const { formErrors } = this.state;

    return (
      <div className="wrapper">
        <div className="form-wrapper">
          <h1>REACT APP</h1>
          <form onSubmit={this.handleSubmit} noValidate>
            <div className="appkey">
              <label htmlFor="appkey"> ENTER APPKEY</label>
              <input
                className={formErrors.appkey.length > 0 ? "error" : null}
                placeholder="appkey"
                type="text"
                name="appkey"
                noValidate
                onChange={this.handleChange}
              />

            </div>

            <div className="createAccount">
            <button type="submit" class="btn btn-info"><i class="fa fa-database"></i> Submit</button>
        <a class="btn btn-secondary" href="/employee/list"><i class="fa fa-list-alt"></i> View</a>            </div>
          </form>

          <form onSubmit={this.handleSubmit} noValidate>
            <div className="appkey">
              <label htmlFor="appkey"> UPDATE APPKEY</label>
              <input
                className={formErrors.appkey.length > 0 ? "error" : null}
                placeholder="appkey"
                type="text"
                name="appkey"
                noValidate
                onChange={this.handleChange}
              />

            </div>

            <div className="createAccount">
            <button type="submit" class="btn btn-info"><i class="fa fa-database"></i> Submit</button>
        <a class="btn btn-secondary" href="/employee/list"><i class="fa fa-list-alt"></i> View </a>            </div>
          </form>

          <form onSubmit={this.handleSubmit} noValidate>
            <div className="appkey">
              <label htmlFor="appkey"> DELETE APPKEY</label>
              <input
                className={formErrors.appkey.length > 0 ? "error" : null}
                placeholder="appkey"
                type="text"
                name="appkey"
                noValidate
                onChange={this.handleChange}
              />

            </div>

            <div className="createAccount">
            <button type="submit" class="btn btn-info"><i class="fa fa-database"></i> Submit</button>
        <a class="btn btn-secondary" href="/employee/list"><i class="fa fa-list-alt"></i> View </a>            </div>
          </form>


        </div>
      </div>
    );
  }
}

export default App;
